import React, { Component } from 'react'

export default class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      todoList: [],
      activeItem: {
        id: null,
        title: '',
        completed: false
      },
      editing : false,
    }
    this.fetchTasks = this.fetchTasks.bind(this)
  }

  componentDidMount() {
    this.fetchTasks()
  }

  fetchTasks(){
    fetch('http://127.0.0.1:8000/api/task-list/')
    .then(response => response.json())
    .then(data => this.setState({ todoList : data}))
  }
  render() {
    const tasks = this.state.todoList
    return (
      <div className="container-fluid bg-primary h-100 ">
        <div className="form-wrapper">
          <form className="p-5" style = {{display: "flex"}}>
            <div className="form-grou w-50">
              <input type="text" className="form-control" placeholder="Enter your task" />              
            </div>            
            <button type="submit" className="btn btn-primary h-75 ml-2">Submit</button>
          </form>
            <table className="table table-light">
            <thead className="thead-light">
              {tasks.map(task =>{
                return(
                  <tr>
                <th scope="col">{task.id}</th>
                <th scope="col">{task.title}</th>
              </tr>
                )
              })}              
              </thead>
              <tbody>
                <tr>
                  <th scope="row">1</th>
                  <td>Mark</td>
                </tr>                
              </tbody>
            </table>
            <br/>
            <br/>
        </div>
      </div>
    )
  }
}
