import React, { Component } from 'react'

export default class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      todoList: [],
      activeItem: {
        id: null,
        title: '',
        completed: false
      },
      editing : false,
    }
    this.fetchTasks = this.fetchTasks.bind(this)
  }

  componentDidMount() {
    this.fetchTasks()
  }

  fetchTasks(){
    fetch('http://127.0.0.1:8000/api/task-list/')
    .then(response => response.json())
    .then(data => this.setState({ todoList : data}))
  }
  render() {
    const tasks = this.state.todoList
    const self = this
    return (
      <div className="container-fluid bg-primary h-100 ">
        <div className="form-wrapper flex-wrapper">
          <form className="p-5" style={{display:'flex'}}>
            <div className="form-group w-50">
              <input type="text" className="form-control" placeholder="Enter your task" />              
            </div>            
            <button type="submit" className="btn btn-primary h-75 ml-2">Submit</button>
          </form>          
        </div>
        <div  id="list-wrapper">         
                {tasks.map(function(task, index){
                  return(
                      <div key={index} className="task-wrapper flex-wrapper" style={{display: 'flex'}}>

                        <div onClick={() => self.strikeUnstrike(task)} style={{flex:7}}>

                            {task.completed === false ? (
                                <span>{task.title}</span>

                              ) : (

                                <strike>{task.title}</strike>
                              )}

                        </div>

                        <div style={{flex:1}}>
                            <button onClick={() => self.startEdit(task)} className="btn btn-sm btn-outline-info">Edit</button>
                        </div>

                        <div style={{flex:1}}>
                            <button onClick={() => self.deleteItem(task)} className="btn btn-sm btn-outline-dark delete">-</button>
                        </div>

                      </div>
                    )
                })}
              </div>
          <br/>
          <br/>
      </div>
    )
  }
}
